# Class 'MVNresponse' (multivariate normal response model)setClass("MVNresponse",contains="response")setMethod("fit","MVNresponse",	function(object,w) {		pars <- object@parameters		fit <- lm.wfit(x=object@x,y=object@y,w=w)		object@parameters$coefficients <- fit$coefficients		object@parameters$Sigma <- cov.wt(x=fit$residuals,wt=w)["cov"]		object <- setpars(object,unlist(pars))		object	})